
import java.util.Scanner;
public class Pattern {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		System.out.println("輸入一個數");
		Scanner input = new Scanner(System.in);
		
		for(int i = input.nextInt() ; i > 0 ; i--){
			System.out.println(" ");
		for(int j = i ; j >= i ; j--)
				System.out.print("*");
		
	}
		
		
	}         }

