
public class array {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	int a = 78;	
    int[] list = {20,8,7,50,30,90,78,15,12,100,7};
    int count = 0;
	for(int i = 0; i <= list.length; i++){
		if(i == a){
			count ++;
		}
		System.out.println("尋找了" + i +" 次才找到78");
		}
		
	
	}

}
